import React from 'react';
import axios from "axios";
import {withRouter, Link} from "react-router-dom";

const url = "http://localhost:4000/api"; 

function getTimeAgo(timestamp) {
  console.log(timestamp);
  let currentTime = new Date().getTime();
  let activityTime = new Date(timestamp).getTime();
  let timeDifference = currentTime - activityTime;
  
  let seconds = Math.floor(timeDifference / 1000);
  let minutes = Math.floor(seconds / 60);
  let hours = Math.floor(minutes / 60);
  let days = Math.floor(hours / 24);
  let months = Math.floor( days / 30);
  let years = Math.floor(months / 12);

  if (years > 0) {
    return years + (years === 1 ? " year ago" : " years ago");
  }
  else if (months > 0) {
    return months + (months === 1 ? " month ago" : " months ago");
  }
  else if (days > 0) {
    return days + (days === 1 ? " day ago" : " days ago");
  } 
  else if (hours > 0) {
    return hours + (hours === 1 ? " hour ago" : " hours ago");
  } 
  else if (minutes > 0) {
    return minutes + (minutes === 1 ? " minute ago" : " minutes ago");
  } 
  else {
    return seconds + (seconds === 1 ? " second ago" : " seconds ago");
  }
}

class Talks extends React.Component{

  constructor(props){
    super(props);
    this.state = {
      message: this.props.location.state?.message,
      talks: [],
      username: ""
    }
  }

  async componentDidMount(){
    try{
        console.log(this.props);
        setTimeout(()=>{
            this.setState({message: ""});
        }, 5000);
        const response = await axios.get(`${url}/talks`, {
            withCredentials: true
        });
        console.log(response);
        this.setState({talks: response.data.talks, username: response.data.username});
        }
    catch(err){
        const {history} = this.props;
        history.push("/talks", {message: err.response.data});
    }
    
}

  render(){
    return (
      <div className='talks mt-5'>
                {this.state.message?(<div className='alert alert-primary'>{this.state.message}</div>):""}
        <h2>Talks</h2>
        <table className='table'>
          <thead>
            <tr>
                <td className='fs-5' colSpan={4}>{this.state.username}'s talks</td>
            </tr>
            <tr className='row'>
                <th className='col-md-3'>Author</th>
                <th className='col-md-3'>Recipient</th>
                <th className='col-md-4'>Message</th>
                <th className='col-md-2'>Time</th>
            </tr>
            </thead>
            <tbody>
            {this.state.talks.map(talk=>{
              return (
                <tr className='row'>
                  <td className='col-md-3'>
                    <div className='col-md-12'>
                <img className='col-md-5' src="data:image/png;base64,"
                    alt="author image"></img></div>
                    <Link className='col-md-2' to={`/profile/${talk.authorName}`}>{talk.authorName}</Link>
                </td>
                <td className='col-md-3'>
                  <div className='col-md-12'>
                <img className='col-md-5' src="data:image/png;base64,"
                    alt="recipient image"></img></div>
                    <Link className='col-md-2' to={`/profile/${talk.recipientName}`}>{talk.recipientName}</Link>
                </td>
                <td className='col-md-4'>{talk.message}</td>
                <td className='col-md-2'>{getTimeAgo(talk.time)}</td>
                </tr>
              )
            })}</tbody>
        </table>
      </div>
    )
  }
}

export default withRouter(Talks);
